import Form from './components/form';
import Todolist from './components/list';
import {useState} from 'react';

function App() {
  const [inputText, setInputText] = useState("");
  const [todos, setTodos] = useState([]);
  const [status, setStatus] = useState("all");
  const [filtredTodos, setFiltredTodos]= useState([]);
  const filtreHandler=() => {
    switch(status){
      case 'completed':
        setFiltredTodos(todos.filter((todo) =>todo.completed))
    }
  }
  return (
    <div clasName='App'>
      <header>

        <h1>Ech bech naych </h1>
      </header>
      <Form setInputText={setInputText}todos={todos} setTodos={setTodos}inputText={inputText} setStatus={setStatus}/>
      <Todolist setTodos={setTodos} todos={todos}></Todolist>

    </div>
  );
}

export default App;
